mapper.delete:
delete
from corpus
where user_no = #{userNo}
  and corpus_name = #{corpusName}
limit 1;

mapper.update:
update corpus
set corpus_name = #{newCorpusName}
where user_no = #{userNo}
  and corpus_name = #{corpusName}
limit 1;

update corpus
set blog_num = blog_num + 1
where user_no = #{userNo}
  and corpus_name = #{corpusName}
limit 1;

update corpus
set blog_num = blog_num - 1
where user_no = #{userNo}
  and corpus_name = #{corpusName}
limit 1;
